import React, { Fragment, useEffect } from 'react'
import PropTypes from 'prop-types'
import { connect } from 'react-redux'
import Spinner from '../layout/Spinner'
import { getProfileById } from '../../actions/profile'
import { Link } from 'react-router-dom'
import ProfileTop from './ProfileTop'
import ProfileAbout from './ProfileAbout'
import ProfileExp from './ProfileExp'
import ProfileEdu from './ProfileEdu'

const Profile = ({ match, getProfileById, profile: { profile, loading }, auth }) => {

    useEffect(() => {
        getProfileById(match.params.id);
    }, [getProfileById]);

    return (
        <Fragment>
            {profile == null || loading ? <Spinner /> :
                <Fragment>
                    <Link to='/profiles' className='btn btn-success'>Back To Profiles</Link>
                    {auth.isAuthenticated && auth.loading == false && auth.user._id == profile.user._id && (<Link to='/edit-profile' className='btn btn-dark' >Edit Profile</Link>)}
                    <div class="profile-grid my-1">
                        <ProfileTop profile={profile} />
                        <ProfileAbout profile={profile} />
                        <div className='profile-exp bg-white p-2'>
                            <h2 className='text-primary'>Experience</h2>
                            {profile.experience.length > 0 ? (<Fragment>
                                {profile.experience.map(exp => (
                                    <ProfileExp key={exp._id} experience={exp} />
                                ))}
                            </Fragment>) : (<h4>No experience credentials</h4>)}
                        </div>
                        <div className='profile-edu bg-white p-2'>
                            <h2 className='text-primary'>Education</h2>
                            {profile.education.length > 0 ? (<Fragment>
                                {profile.education.map(edu => (
                                    <ProfileEdu key={edu._id} education={edu} />
                                ))}
                            </Fragment>) : (<h4>No education details</h4>)}
                        </div>
                    </div>
                </Fragment>
            }
        </Fragment>
    )
}

Profile.propTypes = {
    getProfileById: PropTypes.func.isRequired,
    profile: PropTypes.object.isRequired,
    auth: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
    profile: state.profile,
    auth: state.auth
})

export default connect(mapStateToProps, { getProfileById })(Profile)
